SELECT certificate, COUNT(*) AS movie_count
FROM movies
GROUP BY certificate
ORDER BY movie_count DESC;


-- 3.Director with Most Movies

SELECT director, COUNT(*) AS movie_count
FROM movies
GROUP BY director
ORDER BY movie_count DESC
LIMIT 5;


