SELECT genre, ROUND(AVG(rating), 2) AS avg_rating
FROM movies
GROUP BY genre
ORDER BY avg_rating DESC
Limit 10;

-- 7.Average Rating by Certificate and Genre

SELECT certificate, genre, ROUND(AVG(rating), 2) AS avg_rating
FROM movies
GROUP BY certificate, genre
ORDER BY avg_rating DESC
limit 20;

--8.Filter Movies Rated Above 8 by Top Directors

SELECT "Movie", rating, director
FROM movies
WHERE rating > 8
  AND director IN (
      SELECT director
      FROM movies
      GROUP BY director
      HAVING COUNT(*) >= 3
  );
