--11.Average Runtime by Genre

SELECT 
  genre,
  ROUND(AVG(NULLIF(regexp_replace(runtime, '[^0-9]', '', 'g'), '')::NUMERIC), 2) AS avg_runtime
FROM 
  movies
WHERE 
  runtime IS NOT NULL
GROUP BY genre
ORDER BY avg_runtime DESC;


--12.Rating Distribution Binned (e.g., 0-2, 2-4, etc.)

SELECT 
  CASE 
    WHEN rating < 2 THEN '0-2'
    WHEN rating < 4 THEN '2-4'
    WHEN rating < 6 THEN '4-6'
    WHEN rating < 8 THEN '6-8'
    ELSE '8-10'
  END AS rating_bin,
  COUNT(*) AS count
FROM movies
WHERE rating IS NOT NULL
GROUP BY rating_bin
ORDER BY rating_bin;


