--26.Search for Action Keywords (e.g., "fight", "explosion", "chase")

SELECT "Movie", description
FROM movies
WHERE description ILIKE '%fight%'
   OR description ILIKE '%explosion%'
   OR description ILIKE '%chase%';


--27.Top 5 Most Frequent Words in Descriptions (Full-Text Indexing)

SELECT word, COUNT(*) AS freq
FROM (
  SELECT unnest(string_to_array(lower(regexp_replace(description, '[^\w\s]', '', 'g')), ' ')) AS word
  FROM movies
  WHERE description IS NOT NULL
) AS words
WHERE word <> ''
GROUP BY word
ORDER BY freq DESC
LIMIT 5;


--28.Check for Missing or Invalid Votes

SELECT COUNT(*) AS missing_votes
FROM movies
WHERE votes IS NULL OR TRIM(votes) = '' OR votes ~ '[^0-9]';


-- 29.Clean and Convert to a Numeric Value

ALTER TABLE movies
ALTER COLUMN votes TYPE NUMERIC
USING 
  CASE
    WHEN votes::TEXT ILIKE '%M' THEN REPLACE(REPLACE(votes::TEXT, '$', ''), 'M', '')::NUMERIC * 1000000
    WHEN votes::TEXT ILIKE '%K' THEN REPLACE(REPLACE(votes::TEXT, '$', ''), 'K', '')::NUMERIC * 1000
    WHEN votes::TEXT ~ '^[0-9,]+$' THEN REPLACE(votes::TEXT, ',', '')::NUMERIC
    ELSE NULL
  END;
  
-- 30.Basic Vote Statistics
  
  SELECT 
  MIN(votes) AS min_votes,
  MAX(votes) AS max_votes,
  ROUND(AVG(votes)) AS avg_votes,
  PERCENTILE_CONT(0.5) WITHIN GROUP (ORDER BY votes) AS median_votes
FROM movies
WHERE votes IS NOT NULL;

